import React, { useEffect } from 'react'
import { Modal, Spin, Form } from 'antd'
import UpdateForm from './_form'

const Update = ({
  updateModalVisible,
  updateItem,
  cancelUpdateModal,
  updateItemLoading,
  initialValues,
}) => {
  const [form] = Form.useForm()

  useEffect(() => {
    form.setFieldsValue(initialValues)
  }, [form, initialValues])

  return (
    <Modal
      title="Update User"
      visible={updateModalVisible}
      onCancel={cancelUpdateModal}
      okText="Save"
      confirmLoading={updateItemLoading}
      onOk={() => {
        form
          .validateFields()
          .then(values => updateItem(values, form))
          .catch(err => console.log(err))
      }}
    >
      <Spin spinning={updateItemLoading}>
        <UpdateForm initialValues={initialValues} form={form} />
      </Spin>
    </Modal>
  )
}

export default Update
