import { GET_ERRORS } from "../actions/globalTypes";
const initialState = {};

const errorReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_ERRORS:
            return action.payload;
        default:
            return state;
    }
}
export default errorReducer;
